mapper.select:
select a.id,a.comments,a.title,a.picture,a.channel_id AS channelId,a.category_id categoryId,a.user_id AS userId,a. hits,a.hot,a.status,a.deleted,a.created,a.updated, u.username, GROUP_CONCAT(t.tagname) as tags from cms_article as a left join cms_user u on u.id=a.user_id left join cms_article_tag_middle as atm on atm.aid=a.id LEFT join cms_tag as t on t.id=atm.tid
a.status=1 and a.deleted=0
and a.channel_id=#{channelId}
and a.category_id=#{catId}
group by a.id

select a.id,a.comments,a.content,a.title,a.picture,a.channel_id AS channelId,a.category_id categoryId,a.user_id AS userId, a.hits,a.hot,a.status,a.deleted,a.created,a.updated, cat.`name` as catName , chn.`name` as chnName, GROUP_CONCAT(t.tagname) as tags ,u.username from cms_article as a left join cms_user u on u.id=a.user_id left join cms_channel as chn on chn.id=a.channel_id left join cms_category as cat on cat.id=a.category_id left join cms_article_tag_middle as atm on atm.aid=a.id LEFT join cms_tag as t on t.id=atm.tid
and a.status=#{status}
group by a.id order by a.created desc
